class GlozinCountDownWidgetHandler extends elementorModules.frontend.handlers
  .Base {
  getDefaultSettings() {
    return { selectors: { container: ".glozin-countdown" } };
  }
  getDefaultElements() {
    const e = this.getSettings("selectors");
    return { $container: this.$element.find(e.container) };
  }
  getCountDownInit() {
    jQuery(document.body).trigger("glozin_countdown", this.elements.$container);
  }
  onInit() {
    super.onInit();
    this.getCountDownInit();
  }
}
jQuery(window).on("elementor/frontend/init", () => {
  elementorFrontend.hooks.addAction(
    "frontend/element_ready/glozin-countdown.default",
    (e) => {
      elementorFrontend.elementsHandler.addHandler(
        GlozinCountDownWidgetHandler,
        { $element: e }
      );
    }
  );
  elementorFrontend.hooks.addAction(
    "frontend/element_ready/glozin-banner.default",
    (e) => {
      elementorFrontend.elementsHandler.addHandler(
        GlozinCountDownWidgetHandler,
        { $element: e }
      );
    }
  );
  elementorFrontend.hooks.addAction(
    "frontend/element_ready/glozin-product-deals.default",
    (e) => {
      elementorFrontend.elementsHandler.addHandler(
        GlozinCountDownWidgetHandler,
        { $element: e }
      );
    }
  );
});
